":{\"type\":\"8\",\"fields\":{\"listItem\":\"New Boys & Girls Locker Rooms\"}},\"184\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New Ticketing \\/ Concession \\/ Restroom \\/ Satellite Trainers room and three bay storage facility \"}},\"188\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New (6) post tensioned concrete tennis courts with basketball accommodations \"}},\"180\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New 6 lane (8 straight) synthetic track\"}},\"181\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New full size synthetic turf field with new field lighting system\"}},\"182\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New 1500 seat bleacher system \"}},\"183\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New stadium scoreboard and sound system\"}},\"185\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New girls softball field, dugouts, and scoreboard\"}},\"189\":{\"type\":\"8\",\"fields\":{\"listItem\":\"Section of new Town Bikeway access to facility \"}},\"190\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New Accessible parking \"}},\"191\":{\"type\":\"8\",\"fields\":{\"listItem\":\"Renovated two story press box\"}},\"186\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New baseball field, dugouts, fencing, batting cage and scoreboard\"}},\"192\":{\"type\":\"8\",\"fields\":{\"listItem\":\"Restored historical field signage archway \"}},\"193\":{\"type\":\"8\",\"fields\":{\"listItem\":\"Decorative site lighting \"}},\"187\":{\"type\":\"8\",\"fields\":{\"listItem\":\"New multi-use natural grass athletic fields and irrigation \"}}},\"centered\":\"1\",\"lift\":\"\",\"lightGrayBackground\":\"\"}},\"197\":{\"type\":\"people\",\"enabled\":\"1\",\"fields\":{\"heading\":\"Meet the team involved.\",\"people\":{\"198\":{\"type\":\"22\",\"fields\":{\"person\":[\"35\"],\"role\":[\"42\"]}},\"282\":{\"type\":\"22\",\"fields\":{\"person\":[\"274\"],\"role\":[\"42\"]}}}}},\"195\":{\"type\":\"contact\",\"enabled\":\"\",\"fields\":{\"heading\":\"Let\'s work together! \"}},\"196\":{\"ty